import styled from 'styled-components';

export const Header = styled.header`
    display: flex;
    position: fixed;
    background: #fff;
    padding: 0 2.4rem;
    opacity: 0.95;
    z-index: 200;
    box-shadow: rgba(0,0,0,.5) 0px 1px 40px -8px;
    box-sizing: border-box;
    width: 100%;
    align-items: center;
    justify-align: space-between;
    font-size: 20px;
`;

export const Title = styled.a.attrs({
    href: "/"
})`
    color: tomato;
    height: 4.25rem;
    width: 14.67rem;
    line-height: 4.25rem;
`;

export const Menu = styled.div
