import { useEffect } from 'react';

export default function resetScroll(Comp) {
    const HigherComp = function (props) {
        useEffect(() => (
            props.history.listen(() => {
               window.scrollTo(0, 0); 
            })
        ), []);
        return <Comp {...props}/>
    }
    return HigherComp;
}
