import styled from 'styled-components';

export const Content = styled.div`
    padding: 8rem 0;
    width: 95%;
`;

export const Cover = styled.img.attrs(props => ({
    src: props.src
}))`
    width: 100%;
    margin-bottom: 2rem;
    border-radius: 1rem;
`

export const Title = styled.h2`
    font-weight: bold;
    text-align: center;
    font-size: 2.5rem;
`

export const Msg = styled.ul`

`

export const Desc = styled.div`

`
