import styled from 'styled-components';
import pageArrow from '../../assets/pagearrow.svg';

const Container = styled.ul`
    display: flex
`

const PageItem = styled.li`
    width: 2rem;
    height: 2rem;
    border-radius: 50%;
    color: #3f51b5;
    cursor: pointer;
    text-align: center;
    line-height: 2rem;
    margin: 0 .2rem;
`;

function Pager({total, page, onchange}) {
    return <Container>
        <PageItem>{"<"}</PageItem>
        <PageItem>1</PageItem>
        <PageItem>2</PageItem>
        <PageItem>3</PageItem>
        <PageItem>4</PageItem>
        <PageItem>{">"}</PageItem>
    </Container>
}

export default Pager;
