import {Title} from './styleComps';
import styled from 'styled-components';
import {Link} from 'react-router-dom';

const Button = styled.button`
  display: inline-block;
  color: palevioletred;
  font-size: 1em;
  margin: 1em;
  padding: 0.25em 1em;
  border: 2px solid palevioletred;
  border-radius: 3px;
  display: block;
`;

const TomatoButton = (props) => {
    const types = {
        primary: "blue",
        warning: "yellow",
        error: "red"
    }
    const size = {
        large: "10em",
        small: "0.5em"
    }
    const Btn = styled(Button)`
      color: tomato;
      border-color: tomato;
      cursor: pointer;

      &:hover {
        color: blue
      };
      background: ${props => types[props.type]};
      size: ${props => size[props.size]};
    `;
    console.log(props);
    return <Btn {...props}>{props.children}</Btn>;
}

function Home() {
    return (<>
        <Button href="/">link with button styles</Button>
        <TomatoButton href="/">link with tomato button styles</TomatoButton>
        <TomatoButton type="warning" size="large" href="/" className="something">link with tomato button styles</TomatoButton>
    </>)
}

export default Home;
