import styled from 'styled-components';

export const Content = styled.div`
    padding: 6rem 0;
    width: 95%;
`;

export const Cover = styled.img.attrs(props => ({
    src: props.src
}))`
    width: 100%;
    margin-bottom: 2rem;
`
