import { useEffect } from 'react';

export function resetScroll(Comp) {
    const HigherComp = function (props) {
        useEffect(() => (
            props.history.listen(() => {
               window.scrollTo(0, 0); 
            })
        // eslint-disable-next-line
        ), []);
        return <Comp {...props}/>
    }
    return HigherComp;
}

export function ResetScrollComp({history}) {
    useEffect(() => (
        history.listen(() => {
           window.scrollTo(0, 0); 
        })
    // eslint-disable-next-line
    ), []);
    return null;
}
