import styled from "styled-components";

export const Cover = styled.div`
    height: 100vh;
    background-image: ${props => "url(" + props.src + ")"};
    background-size: 100% 100%;
`;

export const Mask = styled.div`
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    background: rgba(0,0,0,.5);
`;
