import React, { useEffect, useState } from 'react';
import { Content, Cover, Title, Msg, MsgItem, TagItem, Desc } from './styleComps';
import { MiddleContainer } from '../../common/styleComp';
import { getBlogById } from '../../service/Blog';
import * as moment from 'moment';

import Article from './Article';

function Detail(props) {
    const id = props.match.params.id;
    const [blog, setBlog] = useState(null);
    useEffect(() => {
        (async() => {
            if(id === undefined) return ;
            const resp = await getBlogById(id);
            if(resp.data.data === undefined) return ;
            console.log(resp.data.data);
            setBlog(resp.data.data);
        })();
    }, [id]);
    console.log(blog);
    return (<MiddleContainer>
        <Content>
                <Cover src={blog && blog.cover} />
                <Title>{blog && blog.title}</Title>
                <Msg>
                    <MsgItem>{moment(blog.ctime, "MM-DD-YYYY")}</MsgItem>
                    <MsgItem>203 PV</MsgItem>
                    <MsgItem>8 Likes</MsgItem>
                    <TagItem>javascript</TagItem>
                </Msg>
                <Desc>现代的前端项目一般都在采用 ES Module, 不过对于配置文件大多都在使用 CommonJS, 这边文章简析 AMD / CMD / CommonJS / ES Module 的发展史与异同.</Desc>
                <Article data={blog}/>
            </Content>
    </MiddleContainer>)
}

export default Detail;
