import styled from 'styled-components';

export const Container = styled.section`
    width: 50rem;
    height: 20rem;
    border: 1px solid #fff;
    border-radius: .5rem;
    box-shadow: rgba(0, 0, 0, .1) 0px 1px 20px -8px; 
    margin: 3rem auto;
    display: flex;
`

export const Cover = styled.div`
    width: 60%;
    height: 100%;
`

export const Intro = styled.div`
    width: 40%;
`
