import styled, {keyframes} from 'styled-components';
import {Link} from 'react-router-dom';

const shaking = keyframes`
    from {
        rotate: 0;
    }
    25% {
        rotate: -45deg
    }
    50 {
        rotate: 0
    }
    to {
        rotate: 45deg
    }
`;

const StyleLink = styled(Link)`
    cursor: pointer;
    &:hover {
        color: orange;
    };
    & img {
        animation: ${shaking} linear .2s;
    }
`

function TagLink({icon, link, text}) {
    return (
        <StyleLink to={link}>
            <img src={icon} alt=""/>
            <span>{text}</span>
        </StyleLink>
    )
}

export default TagLink;
